package external;

import internal.PaymentGateway;

public class PayPal implements PaymentGateway {
    public void initiateTransaction() {
        System.out.println("Transaction initiated through PayPal.");
    }
    public void cancelTransaction() {
        System.out.println("Transaction canceled in PayPal.");
    }
    public void refund() {
        System.out.println("Refund initiated via PayPal.");
    }
    public void generateReceipt() {
        System.out.println("Receipt generated by PayPal.");
    }
    public void authenticate() {
        System.out.println("Authentication completed in PayPal.");
    }
    public void verifyAccount() {
        System.out.println("Account verified in PayPal.");
    }
    public void settleFunds() {
        System.out.println("Funds settled by PayPal.");
    }
}
